import React from "react";
import store from "../Reducers"
import {deleteTodo} from "../Actions/actions"

const Todos = () => {

  const todos = store.getState();

  const TodoList = 
    todos.map((todo, i) => {
      console.log("LOgged")
      return (
        <div className="todolist" key={i}>
          <span>
              {todo.iscompleted ? <input className="status" type="checkbox" checked onChange={()=>{store.dispatch(deleteTodo(i))}}/>
               :
               <input className="status" type="checkbox" onClick={() => {store.dispatch(deleteTodo(i))}}/>}
          </span>
          {todo.iscompleted ?( <input className="listitem strike" value={todo.todocaption}/>) : (<input className="listitem"  value={todo.todocaption} />)}
          <span className="cross" onClick={() => {store.dispatch(deleteTodo(i))}}>X</span>
        </div>
      );
    })

  return <div>{TodoList}</div>;
};

export default Todos;
