import React, {Component} from 'react';
import store from "../Reducers";
import { showAll, showActive, showCompleted } from "../Actions/actions";
class Filter extends Component{
    render(){
        return(
        <div className="filter" style={{display: this.props.style}}>
            {/* <span className="count">Count</span> */}
            <button className="showAll" onClick={()=>{store.dispatch(showAll())}}>All</button>
            <button className="showActive" onClick={()=>{store.dispatch(showActive())}}>Active</button>
            <button className="showCompleted" onClick={()=>{store.dispatch(showCompleted())}}>Completed</button>
        </div>
        );
    }
}
export default Filter;
