import { createStore } from "redux";

const myreducer = (
  state = [
    {
      todocaption: "action.todocaption",
      iscompleted: false
    }
  ],
  action
) => {
  switch (action.type) {
    case "ADD_TODO":
      return [
        ...state,
        { todocaption: action.todocaption, iscompleted: false }
      ];
    case "DEL_TODO":
      state.splice(action.id, 1);
      return state;
  }
};

const store = createStore(myreducer);

export default store;
