import React, {Component} from 'react';
//import store from "../Reducers";
import { showAll, showActive, showCompleted } from "../Actions/actions";
import { connect } from "react-redux";
class Filter extends Component{
    render(){
        return(
        <div className="filter" style={{display: this.props.style}}>
            {/* <span className="count">Count</span> */}
            <button className="showAll" onClick={this.props.showAll}>All</button>
            <button className="showActive" onClick={this.props.showActive}>Active</button>
            <button className="showCompleted" onClick={this.props.showCompleted}>Completed</button>
        </div>
        );
    }
}
const mapDispatchToProps = dispatch =>({
    showAll: ()=>dispatch(showAll()),
    showActive: ()=>dispatch(showActive()),
    showCompleted: ()=>dispatch(showCompleted())
})

const FilterList = connect(null,mapDispatchToProps)(Filter);
export default FilterList;
