import { createStore } from "redux";
import Todos from "../Components/Todos";
const myreducer = (state = { filter: "", todoArray: [] }, action) => {
  switch (action.type) {
    case "ADD_TODO":
      //   return [
      //     ...state,
      //     { todocaption: action.todocaption, iscompleted: false }
      //   ];
      const newState = {
        filter : state.filter,
        todoArray: [...state.todoArray,{ todocaption: action.todocaption, iscompleted: false }]}
        return newState
      // state.todoArray = [
      //   ...state.todoArray,
      //   { todocaption: action.todocaption, iscompleted: false }
      // ];
      // break;
    case "DEL_TODO":
      state.todoArray.splice(action.id, 1);
      //return state;
      break;
    case "EDIT_TODO":
      state.todoArray[action.id].todocaption = action.todocaption;
      //return state;
      break;
    case "CHECK_TODO":
      state.todoArray[action.id].iscompleted = !state.todoArray[action.id]
        .iscompleted;
      //return state;
      break;
    default:
      break;
  }
  switch (action.filter) {
    case "SHOW_ACTIVE":
      state.filter = "SHOW_ACTIVE";
      break;
    case "SHOW_COMPLETED":
      state.filter = "SHOW_COMPLETED";

      break;
    case "SHOW_ALL":
      state.filter = "SHOW_ALL";
      break;
  }
  return state;
};

const store = createStore(myreducer);

export default store;
