import { createStore } from "redux";
import Todos from "../Components/Todos";
const myreducer = (state = { filter: "",todoArray: []}, action) => {
  switch (action.type) {
    case "ADD_TODO":
    //   return [
    //     ...state,
    //     { todocaption: action.todocaption, iscompleted: false }
    //   ];
        state.todoArray = [
            ...state.todoArray,{todocaption: action.todocaption, iscompleted: false}
        ];
        break;
    case "DEL_TODO":
      state.splice(action.id, 1);
      return state;
    case "EDIT_TODO":
      state[action.id].todocaption = action.todocaption;
      return state;
    case "CHECK_TODO":
      state[action.id].iscompleted = !state[action.id].iscompleted;
      return state;
    case "SHOW_ACTIVE":
      console.log("active clicked");
      return state;
    case "SHOW_COMPLETED":
      console.log("completed clicked");
      return state;
    case "SHOW_ALL":
      console.log("all clicked");
      return state;
    default:
      return state;
  }
};

const store = createStore(myreducer);

export default store;
