const Todos = () => {

  var todos = store.getState();

  const TodoList = todos.length ? (
    todos.map((todo, i) => {
      return (
        <div className="todolist" key={i}>
          <span>
              {todo.iscompleted ? <input className="status" type="checkbox" checked />
               :
               <input className="status" type="checkbox"/>}
          </span>
          {todo.iscompleted ?( <input className="listitem strike" value={todo.todocaption} disabled/>) : (<input className="listitem"  value={todo.todocaption} />)}
          <span className="cross" onClick={() => {() => {store.dispatch(deleteTodo(i))}}}>X</span>
        </div>
      );
    })
  ) : (
    <p className="listitem">No Left Todo </p>
  );
  return <div>{TodoList}</div>;
};


export default Todos;
